mapper.insert:
		
			SELECT 
                  IF(SUBSTR(MAX(cust_no), 1, 10) = DATE_FORMAT(now(), '%Y%m%d%H')
                     , MAX(cust_no)+1
                     , concat(DATE_FORMAT(now(), '%Y%m%d%H'),'00001')) cust_no
                  FROM tb_cust USE INDEX(PRIMARY)
		
		insert into tb_cust (
							cust_no
							,cust_name
							,resident_no
							,chart_no
							,cust_id
							,visit_cd
							,visit_dtl_cd
							,visit_cn
							,rec_per
							,remark_cn
		)
		values(
			#{cust_no}
           	,#{cust_name}
           	,#{resident_no}
           	,#{chart_no}
           	,#{cust_id}
           	,#{visit_cd}
           	,#{visit_dtl_cd}
           	,#{visit_cn}
           	,#{rec_per}
           	,#{remark_cn}
		)

mapper.update:
update tb_cust
set
	cust_id = #{cust_id}
	,visit_cd = #{visit_cd}
	,visit_dtl_cd = #{visit_dtl_cd}
	,visit_cn = #{visit_cn}
	,rec_per = #{rec_per}
	,remark_cn = #{remark_cn}
where cust_no = #{cust_no}
