mapper.update:
update tb_task
set 
    subject   = #{subject}
  , cust_no   = #{cust_no}
  , emp_no    = #{emp_no}
  , next_day  = #{next_day}
  , dtype_cd  = #{dtype_cd}
  , lead_no	  = #{lead_no}
  , oppty_no  = #{oppty_no}
  , score_cd  = #{score_cd}
  , location  = #{location}
  , remark_cn = #{remark_cn}
where task_no = #{task_no}
